import org.junit.Test;

import static org.junit.Assert.assertEquals;

public class CaesarCipherTest extends BaseIOValidationTest {

  private static final String WRONG_ARGUMENTS = "Wrong arguments";
  private static final String WRONG_OUTPUT = "Wrong output";

  @Test
  public void testWrongArguments() {
    String[] argsSet ={
      "",
      "1 2 3 4",
      "-x 3 abc",
      "-e 999999999999999999999 abc"
    };

    for (String args : argsSet) {
      CaesarCipher.main(args.split(" "));
      assertEquals(TestMsg.of(1, args, WRONG_ARGUMENTS).toString(), WRONG_ARGUMENTS + "\r\n", output.toString());
      resetOutput();
    }
  }

  @Test
  public void testSolution() {
    String[][] argsResSet = {
      {"-e 1 dwwdfndwrqfh", "exxegoexsrgi"},
      {"-d 1 exxegoexsrgi", "dwwdfndwrqfh"},
      {"-e 35 abcdefghgklzABCDEFGHGKLZ", "jklmnopqptuiJKLMNOPQPTUI"},
      {"-d 35 jklmnopqptuiJKLMNOPQPTUI", "abcdefghgklzABCDEFGHGKLZ"},
      {"-d 35 abcdefghgklzABCDEFGHGKLZ", "rstuvwxyxbcqRSTUVWXYXBCQ"},
      {"-d 108 abzyABZY", "wxvuWXVU"},
      {"-e 108 abzyABZY", "efdcEFDC"},
      {"-e 3 IfinishedtestingYahooooo", "LilqlvkhgwhvwlqjBdkrrrrr"},
      {"-d 3 LilqlvkhgwhvwlqjBdkrrrrr", "IfinishedtestingYahooooo"},
      {"-e 260000000 ", ""},
    };

    for (String[] argsRes : argsResSet) {
      CaesarCipher.main(argsRes[0].split(" "));
      assertEquals(TestMsg.of(2, argsRes, WRONG_OUTPUT).toString(), argsRes[1] + "\r\n", output.toString());
      resetOutput();
    }
  }


}